import React, {useState, useContext, useEffect} from 'react'
import Comment from '../components/Comment'
import {AuthContext} from '../contexts/AuthContext'

const Comments = ({blog}) => {
    const [comments, setComments] = useState([])
    const {authState} = useContext(AuthContext)
    useEffect(()=>{
        console.log("Sending fetch request for blog id", blog)
        if(authState)
        fetch("http://localhost:2000/blog/comment/fetch",{
        method:'POST',
        body:JSON.stringify({blog}),
        headers:{
            "Content-Type": "application/json",
            "Authorization": `Bearer ${authState}`
        }
      }).then(data=>data.json())
        .then(data=>setComments(data))
        .catch(err=>console.log("err"))
    },[authState])
  return (
    <div>
        {comments.map(dat=><Comment id={dat.id} content={dat.content} author={dat.commenter} likes={dat.likes} liked={dat.liked} blog={blog}/>)}
        
    </div>
  )
}

export default Comments
