import React, {useState} from 'react'
import Comment from '../components/Comment'
const Comments = ({thread, lookup, blog, getComment, level=0, idx="top", page=1}) =>{
    // const [more, setMore] = useState(true)
    // const [page, setPage] = useState(1)
    return(<>
        {thread[idx]!==undefined && (
            thread[idx].map(node=>{
                const $found=lookup[node]
                return <>
                <Comment id={$found.id} author={$found.commenter} content={$found.content} likes={$found.likes} liked={$found.liked} selfw={$found.selfw} blog={blog} deleted={$found.deleted} level={level+1} getComment={getComment} page={page}/>
                <br />
                <Comments thread={thread} lookup={lookup} level={level+1} idx={node} blog={blog} getComment={getComment} page={page+1}/>
                {/* {more ? <div style={{marginLeft:`${level+1}00px`}} onClick={()=>{getComment($found.id, page);setPage(page+1)}}>Load More Replies{page}</div> : "No more comments to show"}  */}
                </>
            })
            )}
    </>)
}
export default Comments
