import React, {useState, useContext} from 'react'
import {AuthContext} from '../contexts/AuthContext'

const Comment = ({id, content, likes, author, liked}) => {
  const {authState} = useContext(AuthContext)
  const [likeStatus, setLikeStatus] = useState(liked)

  function likeComment(event){
    fetch("http://localhost:2000/blog/comment/like",{
            body:JSON.stringify({comment:id}),
            headers:{ 
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"PATCH"
    }).then(data=>{
      if(data.ok)
        setLikeStatus(prev=>!prev)
    }).catch(err=>console.log("An error occured"))
  }
  
  return (
    <div className='comment' id={id}>
    <div>{author}</div>
    <div className="content">{content}</div>
    <div className="likes" >{likes} likes</div>
    <div className="like" onClick={(event)=>likeComment(event)}>{likeStatus?"liked":"like"}</div>
    </div>
  )
}

export default Comment
