import React, {useState, useEffect} from 'react'
import Card from '../components/Card'
const Home = () => {
  const [page, setPage ]= useState(1)
  const [blogs, setBlogs] = useState([])
  useEffect(()=>{
    fetch(`http://localhost:2000/blog/getBlogs?page=${page}`)
    .then(data=>data.json())
    .then(res =>setBlogs(res))
    .catch(err=>console.error(err))
  }, [page])

  return (
    <>
    <div>
      <h2>Trending</h2>
      <section className="popular">
        {blogs.map(blog=><Card key={blog._id} title={blog.title} content={blog.content} author={blog.author} postedOn={blog.createdAt}/>)}
      </section>
    </div>
    <div>
      <h2>Based on your interests</h2>
      <section className="forYou">
      </section>
    </div>
    </>
  )
}

export default Home
