import React from 'react'

const Flash = ({message, classname, id}) => {
  return (
    <div className={`flash ${classname}`} id={id}>
        <div className="message">
            {message}
        </div>
        <i onClick={(event)=>{
            document.getElementById(id).remove();
        }}>X</i>
    </div>
  )
}

export default Flash
