import React, {useState, useContext, useEffect} from 'react'
import ReplyField from './ReplyField'
import {AuthContext} from '../contexts/AuthContext'

const Comment = ({id, author, content, likes, liked, selfw, blog}) => {
  const {authState} = useContext(AuthContext)
  const [clickedReply, setClickedReply] = useState(false)
  const [likedByMe, setLikedByMe] = useState(liked)
  const [totalLikes, setTotalLikes] = useState(likes)
  function like(){
    console.log(id)
    fetch("http://localhost:2000/blog/comment/like",{
      method:'PATCH',
      body:JSON.stringify({comment:id}),
      headers:{
        "Content-Type": "application/json",
        "Authorization": `Bearer ${authState}`
    }
    }).then(data=>{
      if(data.ok){
        if(likedByMe)
          setTotalLikes(prev=>prev-1)
        else
          setTotalLikes(prev=>prev+1)
        setLikedByMe(prev=>!prev)
      }
    }).catch(err=>console.log(err))
  }
  function reply(){
    // setClickedReply(true)
  }
  function deleteComment(){

  }
  return (
    <div className='comment' id={id}>
      <div>{author}</div>
      <div>{content}</div>
      <div onClick={like}>{totalLikes} {likedByMe? 'r' : 'w'}</div>
      {selfw && <button onClick={deleteComment}>Delete</button>}
      <div onClick={reply}>Reply to this comment</div>
      {clickedReply && <ReplyField parent={id} blog={blog}/>}
    </div>
  )
}
export default Comment
