import React from 'react'

function toggle(event){
    const text = event.target.innerText
    if(text=="&#xe106;"){
        event.target.innerText = "&#128065;"
        event.target.parentElement.firstElementChild.type = 'text'
    }
    else{
        event.target.innerText = "&#xe106;"
        event.target.parentElement.firstElementChild.type = 'password'
    }
}

function getComponent(type, options, name, label){
    
}

const Formfield = ({type, name, label, error, options}) => {
    if(type=='button')
        return (<button type="submit" value={label} name={name} id={name}/>)
  return (
    <div className="formfield">
        <label htmlFor={name}>
            <h3>{label}</h3>
        </label>
        {type==='password' && <>
            <input type="password" name={name} id={name}/>
            <i className="toggle" onClick={(event)=>toggle}>&#128065;</i>
        </>}
        
        {(() => {
                console.log("Ok")
                switch(type){
                    case 'password':
                        return(<>
                            <input type="password" name={name} id={name}/>
                            <i className="toggle" onClick={(event)=>toggle}>&#128065;</i>
                        </>)
                    break;
                    case 'select':
                        <select name={name} id={name}>
                            <option value="" selected default></option>
                            {options.map(option=>
                                <option value={option.toLowerCase()} key={option.toLowerCase()}>{option}
                                </option>
                            )}
                        </select>
                    break;
                    case 'button':
                        return (<button type="submit" value={label} name={name} id={name}/>)
                    break;
                    case 'textarea':
                        <textarea name={name} id={name}></textarea>
                    break;
                    default:
                        <input type={type} name={name} id={name}></input>
                    break;
                }
      })()}
        <div className="error">{error}</div>
    </div>
  )
}

export default Formfield
