import React, {useState, useEffect, useContext} from 'react'
import {AuthContext} from '../contexts/AuthContext'

const Blog = ({id}) => {

    const {authState} = useContext(AuthContext)
    const [blogobj, setBlogobj] = useState("")
    useEffect(()=>{
        console.log(`Bearer ${authState}`)
        fetch("http://localhost:2000/blog/read", {
            method:'POST',
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            body:JSON.stringify({blog:id})
        }).then(data=> data.json()) 
        .then(data=>{
            console.log(data)
            setBlogobj(data)
        })
        .catch(err=>console.log(err))
    },[])

  return (
   <div className="blog">
    <h1>{blogobj.title}</h1>
    <div className="details">
        <div className="author">Written by {blogobj.author}</div>
        <div className="date">Published {blogobj.publishedAt}</div>
    </div>
    <article>
        {blogobj.content}
    </article>
    <div className="details">
        <div className="likes">{blogobj.likes} likes</div>
    </div>
    <div className="comments">
        <h2>Comments</h2>
    </div>
   </div>
  )
}

export default Blog
