import React from 'react'

const Card = ({title, content, likes, author, published, blogId, cover}) => {

  return (
    <div className="card" id={blogId}>
        <div className="cover">
        <img src={`http://localhost:2000/get_image?image=${cover}`} alt="" />
        </div>
        <div className="details">
        <h2 className="title">{title}</h2>
        <div className="second">
        {liked}<i class="fas fa-heart" style={{color:red}}></i>
            <div className="liked" style={{flexBasis:"15%"}}>{likes}<span style={{color:"red"}}>&#10084;</span></div>
            <div className="date" style={{flexBasis:"30%"}}>{published}</div>
            <div className="author" style={{flexBasis:"50%", textAlign:"right"}}>{author}</div>
        </div>
        <div className="content truncate">
            {content}
        </div>
        </div>
    </div>
  )
}

