import React, {useState} from 'react'

const Blog = ({author, published, likes, id, content, title}) => {
   
    const [like, setLike] = useState(like)
    const [dislike, setDislike] = useState(dislike)

    async function likeBlog(event){
      fetch("http://localhost:2000/blog/like", {
          method: 'POST',
          body: JSON.stringify({like:1})
      })
    }

    useEffect(()=>{
        fetch("http://localhost:2000/blog/like")
    }, [])

return (
        
    <div className="blog" id={id}>
        <h1>{title}</h1>
        <section className="details">
            <div className="author">Written by {author}</div>
            <div className="publishes">Published on {published}</div>
        </section>
        <article>
            {content}
        </article>
        <section className="bottom">
            <div className="likes" name="like" onClick={(event)=>likeBlog(event)}>{likes} likes</div>
            <div className="comments">
                <h2>Comments</h2>
            </div>
        </section>
    </div>
  )
}

export default Blog
