import React, {useContext, useState, useEffect} from 'react'
import {AuthContext} from '../contexts/AuthContext'
import {useParams} from 'react-router-dom';
import Comment from '../components/Comment'
const Threads = ({thread, lookup, level=0, idx="top"}) =>{
    return(<>
        {thread[idx]!==undefined && (
            thread[idx].map(node=>{
                const $found=lookup[node]
                return <>
                <Comment id={$found.id} author={$found.commenter} content={$found.content} likes={$found.likes} liked={$found.liked} selfw={$found.selfw} blog={$found.blog} level={level+1}/>
                <Threads thread={thread} lookup={lookup} level={level+1} idx={node} />
                </>
            })
        )}
    </>)
}
const Blog = () => {
    const {authState} = useContext(AuthContext)
    const [details, setDetails] = useState({})
    const [liked, setLiked] = useState(0)
    const {blogId} = useParams()
    const [thread, setThread] = useState({})
    const [lookup, setLookup] = useState({})
    const [disp, setDisp] = useState({com:'',level:0,idx:"top"})
    const id = blogId

    function display(com, level=0, idx="top"){
        com[idx]!==undefined && (
            com[idx].map(node=>{
            const $found = lookup[node]
            return <>
                <Comment id={$found.id} author={$found.commenter} content={$found.content} likes={$found.likes} liked={$found.liked} selfw={$found.selfw} blog={$found.blog} level={level+1}/>
                {display(com, level+1, node)}
            </>
        }))
    }


    function getComments(parent=undefined){
        fetch("http://localhost:2000/blog/comment/fetch", {
            body:JSON.stringify({
                parent,
                blog:id
            }),
            method:"POST",
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            }
        })
        .then(data=>data.json())
        .then(data=>{
            let to_push = []
            data.map(datum=>{
                to_push.push(datum.id)
                setLookup(prev=>({...prev, [datum.id]:datum}))
            })
            if(parent === undefined)
                parent = "top"
            if([parent] in thread)
                setThread({...thread, [parent]:[...thread[[parent]], ...to_push]})
            else
                setThread({...thread, [parent]:to_push})
        }).catch(err=>console.log("An error occured", err)) 
    }

    function likeBlog(event){
        const action = event.target.id
        let track = 0
        if((liked===-1 && action==='dislike')|| (liked===1 && action==='like'))
            track = 0;
        else
        track = action==='like'?1:-1
        fetch("http://localhost:2000/blog/like",{
            body:JSON.stringify({blog:id, liked:track}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"PATCH"
        })
        .then(data=>{
            if(data.ok)
            setLiked(track)
        }).catch(err=>console.log("An error occured"))
    }

    useEffect(()=>{
        if(!authState)
            return;
        fetch("http://localhost:2000/blog/read",{
            body:JSON.stringify({blog:id}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"POST"
        })
        .then(data=> data.json())
        .then(data=>{
            setDetails(data)
            setLiked(data.liked)
            getComments()
        })
        .catch(err=>console.log(err))
        getComments()
    }, [authState])

    useEffect(()=>{
        display(thread)
    }, [thread])

  return (
    <div>
        <h1>{details.title}</h1>
        <div className="details">
            <div>Written by {details.author}</div>
            <div>On {new Date(details.publishedAt).toLocaleString("en-US", {
                year:"numeric", month:"long", day:"numeric"
            })}</div>
        </div>
        <article>
            {details.content}
        </article>
        <div className="details">
            <div>{details.likes} likes</div>
            <div className="like" id = "like" onClick={(event)=>likeBlog(event)}>{liked===1? "liked" : "+"}</div>
            <div className="like" id = "dislike" onClick={(event)=>likeBlog(event)}>{liked===-1? "disliked" : "-"}</div>
        </div> 
        <div className="comments">
            <h2>Comments</h2>
            <Threads thread={thread} lookup={lookup}/>
        </div>
    </div>
  )
}

export default Blog
