  }
  
  return (
    <>
    <div>
      <h2>Trending</h2>
      <section className="popular">
        <Link to="">
          {blogs.map(blog=><Card blogId={blog.id} likes={blog.likes} title={blog.title} content={blog.content} author={blog.author} published={blog.publishedAt} onClick={(event)=>openBlog(blog.id)}/>)}
        </Link>
      </section>
      
    </div>
    </>
  )
}

export default Home
