import React, {useState, useEffect} from 'react'

function like(){
    console.log("I have liked this comment")
}

function comment(){
    console.log("reply to this comment")
}

const Comment = ({content, level, author, likes}) => {



  return (
    <div className='comment' style={{padding:`${level}px`}}>
        <div className="author">{author}</div>
        <div className="content">{content}</div>
        <div className="actions">
            <span className="like" onClick={(event)=>like(event)}>{likes} heart</span>
            <span className="reply" onClick={(event)=>comment(event)}>Reply</span>
        </div>
    </div>
  )
}

export default Comment
