import React from 'react'

const Comment = ({id, comment, commenter, likes}) => {
  return (
    <div className="comment" id={id}>
      <div>{commenter}</div>
      <div>{comment}</div>
      <div onClick={likeComment}>{likes} Likes</div>
      <div className="actions">
        reply
      </div>
      {commenter===id && <button>Delete</button>}

    </div>
  )
}

export default Comment
