import React, {useState, useContext, useEffect} from 'react'
import Comment from '../components/Comment'
import {AuthContext} from '../contexts/AuthContext'

const Comments = () => {
    const [comments, setComments] = useState([])
    const {authState} = useContext(AuthContext)
    useEffect(()=>{fetch("http://localhost:2000/blog/comment/fetch",{
        method:'PATCH',
        body:JSON.stringify({blog:'3a2ac8b9-1731-11ee-bd29-f80dac236bf2'}),
        headers:{
          "Authorisation": `Bearer ${authState}`,
          "Content-type": "application/json"
        }
      }).then(data=>data.json())
        .then(data=>setComments(data))
        .catch(err=>console.log("err"))
    },[authState])
  return (
    <div>
        {comments.map(dat=><Comment id={dat.id} content={dat.content} author={dat.author} likes={dat.likes} liked={dat.liked}/>)}
        
    </div>
  )
}

export default Comments
