import React, {useContext, useState, useEffect} from 'react'
import {AuthContext} from '../contexts/AuthContext'
import {useParams} from 'react-router-dom';
import Comment from '../components/Comment'

const Blog = () => {
    function display(com, level=0, idx="top"){
        if(com[idx]===undefined)
            return
       com[idx].map(node=>{
        const $found = lookup[node]
        return <Comment id={$found.id} author={$found.commenter} content={$found.content} likes={$found.likes} liked={$found.liked} selfw={$found.selfw} blog={$found.blog} level={level+1}/> 
        display(com, level+1, node)
       })
    }

    const {authState} = useContext(AuthContext)
    const [details, setDetails] = useState({})
    const [liked, setLiked] = useState(0)
    const {blogId} = useParams()
    const [thread, setThread] = useState({})
    let lookup = []
    const id = blogId

    function getComments(parent=null){
        fetch("http://localhost:2000/blog/comment/fetch", {
            body:JSON.stringify({
                parent,
                blog:id
            }),
            method:"POST",
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            }
        })
        .then(data=>data.json())
        .then(data=>{
            let to_push = []
            data.map(datum=>{
                to_push(datum.id)
                lookup[datum.id]= datum
            })
            if(parent in thread)
                thread = {...thread, parent:[...thread[parent], ...to_push]}
            else
                thread[parent] = to_push
        }).catch(err=>console.log("An error occured", err)) 
    }

    function likeBlog(event){
        const action = event.target.id
        let track = 0
        if((liked===-1 && action==='dislike')|| (liked===1 && action==='like'))
            track = 0;
        else
        track = action==='like'?1:-1
        fetch("http://localhost:2000/blog/like",{
            body:JSON.stringify({blog:id, liked:track}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"PATCH"
        })
        .then(data=>{
            if(data.ok)
            setLiked(track)
        }).catch(err=>console.log("An error occured"))
    }

    useEffect(()=>{
        if(!authState)
            return;
        fetch("http://localhost:2000/blog/read",{
            body:JSON.stringify({blog:id}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"POST"
        })
        .then(data=> data.json())
        .then(data=>{
            setDetails(data)
            setLiked(data.liked)
        })
        .catch(err=>console.log(err))
    }, [authState])


  return (
    <div>
        <h1>{details.title}</h1>
        <div className="details">
            <div>Written by {details.author}</div>
            <div>On {new Date(details.publishedAt).toLocaleString("en-US", {
                year:"numeric", month:"long", day:"numeric"
            })}</div>
        </div>
        <article>
            {details.content}
        </article>
        <div className="details">
            <div>{details.likes} likes</div>
            <div className="like" id = "like" onClick={(event)=>likeBlog(event)}>{liked===1? "liked" : "+"}</div>
            <div className="like" id = "dislike" onClick={(event)=>likeBlog(event)}>{liked===-1? "disliked" : "-"}</div>
        </div>
        <div className="comments">
            <h2>Comments</h2>
            {thread}
        </div>
    </div>
  )
}

export default Blog
