import React, {useState, useContext, useEffect} from 'react'
import ReplyField from './ReplyField'
import {AuthContext} from '../contexts/AuthContext'

const Comment = ({id, author, content, likes, liked, selfw, blog, level}) => {
  const {authState} = useContext(AuthContext)
  const [clickedReply, setClickedReply] = useState(false)
  const [likedByMe, setLikedByMe] = useState(+liked)
  const [totalLikes, setTotalLikes] = useState(+likes)
  const [more, setMore] = useState(true)

  function like(){
    console.log(id)
    fetch("http://localhost:2000/blog/comment/like",{
      method:'PATCH',
      body:JSON.stringify({comment:id}),
      headers:{
        "Content-Type": "application/json",
        "Authorization": `Bearer ${authState}`
    }
    }).then(data=>data.json())
    .catch(err=>console.log(err))
  }
  function reply(){
    setClickedReply(true)
  }
  function deleteComment(){

  }
  function loadMore(){
    console.log(authState, id, blog)
    fetch("http://localhost:2000/blog/comment/fetch",{
      method:"POST",
      body:JSON.stringify({parent:id, blog}),
      headers:{
        "Content-Type": "application/json",
        "Authorization": `Bearer ${authState}`
    }
    })
    .then(data=>data.json())
    .then(data=>{
      let to_push = []
      data.map(datum=>{
          to_push.push(datum.id)
          setLookup(prev=>({...prev, [datum.id]:datum}))
      })
      if(parent === undefined)
          parent = "top"
      if([parent] in thread)
          setThread({...thread, [parent]:[...thread[[parent]], ...to_push]})
      else
          setThread({...thread, [parent]:to_push})
  }).catch(err=>console.log("An error occured", err)) 
  }
  return (
    <div className={`comment ${level}`} id={id}>
      <div>{author}</div>
      <div>{content}</div>
      <div onClick={like}>{totalLikes} {likedByMe? 'r' : 'w'}</div>
      {selfw && <button onClick={deleteComment}>Delete</button>}
      <div onClick={reply}>Reply to this comment</div>
      {clickedReply && <ReplyField parent={id} blog={blog}/>}
      {more ? <div onClick={loadMore}>Load More Replies</div> : "No more comments to show"} 
    </div>
  )
}
export default Comment
