import React, {useContext, useState, useEffect} from 'react'
import {AuthContext} from '../contexts/AuthContext'
const Blog = ({id}) => {
    const {authState} = useContext(AuthContext)
    const [details, setDetails] = useState({})
    const [liked, setLiked] = useState(0)

    function likeBlog(event){
        const action = event.target.name
        let liked = 0
        if((liked===-1 && action==='dislike')|| (liked===1 && action==='like'))
            liked = 0;
        else
            liked = action==='like'?1:-1
        fetch("http://localhost:2000/blog/like",{
            body:JSON.stringify({blog:id, liked}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"PATCH"
        })
        .then(data=>{
            if(data.ok)
                setLiked(liked)
        }).catch(err=>console.log("An error occured"))
    }

    useEffect(()=>{
        fetch("http://localhost:2000/blog/read",{
            body:JSON.stringify({blog:id}),
            headers:{
                "Content-Type": "application/json",
                "Authorization": `Bearer ${authState}`
            },
            method:"POST"
        })
        .then(data=> data.json())
        .then(data=>{
            setDetails(data)
            setLiked(data.liked)
        })
        .catch(err=>console.log(err))
    }, [authState])

  return (
    <div>
        <h1>{details.title}</h1>
        <div className="details">
            <div>{details.auhtor}</div>
            <div>{details.publishedAt}</div>
        </div>
        <article>
            {details.content}
        </article>
        <div className="details">
            <div>{details.likes} likes</div>
            <div className="like" name = "like" onClick={(event)=>likeBlog(event)}>{liked==1? "liked" : "+"}</div>
            <div className="like" name = "dislike" onClick={(event)=>likeBlog(event)}>{liked==-1? "disliked" : "-"}</div>
        </div>
    </div>
  )
}

export default Blog
