import React from 'react'

function toggle(event){
    const text = event.target.innerText
    console.log(event.target)
    if(text=="&#xe106;"){
        event.target.innerHTML = "&#128065;"
        event.target.previousElementSibling.type = 'text'
    }
    else{
        event.target.innerHTML = "&#xe106;"
        event.target.previousElementSibling.type = 'password'
    }
}

const Formfield = ({type, name, label, error, options}) => {
    if(type=='button')
        return (<button type="submit" value={label} name={name} id={name}/>)
  return (
    <div className="formfield">
        <label htmlFor={name}>
            <h3>{label}</h3>
        </label>
        {["text", "number", "email"].includes(type) && <input type={type} name={name} id={name}></input>}
        {type==='textarea' && <textarea name={name} id={name}></textarea>}
        {type==='password' && <>
            <input type="password" name={name} id={name}/>
            <i className="toggle" onClick={(event)=>toggle(event)}>&#128065;</i>
        </>}
        {type==='select' && <select name={name} id={name}>
            <option value="" selected default></option>
            {options.map(option=>
                <option value={option.toLowerCase()} key={option.toLowerCase()}>{option}
                </option>
            )}
        </select>}
        <div className="error">{error}</div>
    </div>
  )
}

export default Formfield
