import React from 'react'
import Formfield from '../components/Formfield'
import useAdd from '../hooks/useAdd'

const AddBlog = () => {
  const {err, handlePublish} = useAdd()
  return (
    <form className="addBlog" onSubmit={handlePublish}>
        <h2>Write your blog</h2>
        <div className="error">{error.general}</div>
        <Formfield name="title" label="Title"  error={err.title} options={undefined} type="text"/>
        <Formfield name="content" label="Content"  error={err.content} options={undefined} type="textarea"/>
        <Formfield name="publish" label="Publish" error={err.publish} options={undefined} type="button"/>
    </form>
  )
}

export default AddBlog
