  }

  return (
    <div className={`comment ${level}`} style={{marginLeft:`${level}00px`}} id={id}>
      <div>{author}</div>
      <div>{content}</div>
      <div onClick={like}>{totalLikes} {likedByMe? 'r' : 'w'}</div>
      {selfw && <button onClick={deleteComment}>Delete</button>}
      <div onClick={reply}>Reply to this comment</div>
      {clickedReply && <ReplyField parent={id} blog={blog}/>}
      {/* {more ? <div style={{marginLeft:`${level+1}00px`}} onClick={()=>{getComment(id, page); setPage(prev=>prev+1)}}>Load More Replies{page}</div> : "No more comments to show"}  */}
      {/* {more ? <div style={{marginLeft:`${level+1}00px`}} onClick={loadComments}>Load More Replies{page}</div> : "No more comments to show"}  */}
    </div>
  )
}
export default Comment
