import React, { useState } from 'react'

const tx = document.getElementsByTagName("textarea");
for (let i = 0; i < tx.length; i++) {
  tx[i].setAttribute("style", "height:" + (tx[i].scrollHeight) + "px;overflow-y:hidden;");
  tx[i].addEventListener("input", OnInput, false);
}

function OnInput() {
  this.style.height = 0;
  this.style.height = (this.scrollHeight) + "px";
}

const Formfield = ({type, name, label, error, options}) => {
    const [passwordType, setpasswordType] = useState('password')
    function toggle(event){
        if (passwordType==='password') setpasswordType('text'); else setpasswordType('password')
    }
    if(type==='button')
        return (<div className="formfield">
            <button type="submit" name={name} id={name}>{label}</button>
            </div>)
  return (
    <div className="formfield">
        <label htmlFor={name}>
            <h3>{label}</h3>
        </label>
        {["text", "number", "email"].includes(type) && <input type={type} name={name} id={name}></input>}
        {type==='textarea' && <textarea name={name} id={name} onInput={handleSize}></textarea>}
        {type==='password' && 
        <>
            <input type={passwordType} name={name} id={name}/>
            <span className="toggle" onClick={(event)=>toggle(event)}>{passwordType==='password' ? "Show Password" : "Hide Password"}</span>
        </>}
        {type==='select' && <select name={name} id={name}>
            <option value="" selected default></option>
            {options.map(option=>
                <option value={option.toLowerCase()} key={option.toLowerCase()}>{option}
                </option>
            )}
        </select>}
        <div className="error">{error}</div>
    </div>
  )
}

export default Formfield
